import FormWrapper, { FormButtons } from "./styles/FormWrapper"
import { FormProvider, useForm } from "react-hook-form"
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from 'yup';
import yupFormSchemas from "modules/shared/yup/yupFormSchemas";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import selectors from '../../modules/auth/authSelectors';
import actions from '../../modules/auth/authActions';
import { Button, Grid, TextField } from "@material-ui/core";
import InputFormItem from "view/shared/form/items/InputFormItem";
import SaveIcon from '@material-ui/icons/Save';
import UndoIcon from '@material-ui/icons/Undo'
import CloseIcon from '@material-ui/icons/Close'

const schema = yup.object().shape({
    firstName: yupFormSchemas.string(
        'Имя',
        {
            max: 80
        }
    ),
    lastName: yupFormSchemas.string(
        'Фамилия',
        {
            max: 80
        }
    ),
    phoneNumber: yupFormSchemas.string('Номер Телефона')
        .matches(/^((\+\d{1,3}(-| )?\(?\d\)?(-| )?\d{1,3})|(\(?\d{2,3}\)?))(-| )?(\d{3,4})(-| )?(\d{4})(( x| ext)\d{1,5}){0,1}$/,
            'Неправильный формат'
        ),
    avatars: yupFormSchemas.images(
        'Аватар',
        {
            max: 1
        }
    )
})

export default function ProfileFormPage(props) {
    const dispatch = useDispatch();

    const saveLoading = useSelector(
        selectors.selectLoadingUpdateProfile
    )

    const currentUser = useSelector(
        selectors.selectCurrentUser
    )

    const [initialValues] = useState(() => {
        const record = currentUser || {};

        return {
            firstName: record.firstName,
            lastName: record.lastName,
            phoneNumber: record.phoneNumber,
            avatars: record.avatars || []
        }
    })

    const form = useForm({
        resolver: yupResolver(schema),
        mode: 'all',
        defaultValues: initialValues
    });

    const onSubmit = (values) => {
        dispatch(actions.doUpdateProfile(values))
    }

    const onReset = () => {
        Object.keys(initialValues).forEach((key) => {
            form.setValue(key, initialValues[key]);
        })
    }

    return(
        <FormWrapper>
            <FormProvider {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)}>
                    <Grid spacing={2} container>
                        <Grid item lg={6} md={8} sm={12} xs={12}>
                            <InputFormItem 
                                name="firstName"
                                label="Имя"
                                autoComplete="firstName"
                                autoFocus
                            />
                        </Grid>
                        <Grid item lg={6} md={8} sm={12} xs={12}>
                            <InputFormItem 
                                name="lastName"
                                label="Фамилия"
                                autoComplete="lastName"
                            />
                        </Grid>
                        <Grid item lg={6} md={8} sm={12} xs={12}>
                            <TextField
                                id="email"
                                name="email"
                                label="email"
                                value={currentUser.email}
                                fullWidth
                                margin="normal"
                                InputProps={{
                                    readOnly: true
                                }}
                                InputLabelProps={{
                                    shrink: true
                                }}
                                variant="outlined"
                                size="small"
                            />
                        </Grid>
                        <Grid item lg={6} md={8} sm={12} xs={12}>
                            <InputFormItem 
                                name="phoneNumber"
                                label="номер телефона"
                                autoComplete="phoneNumber"
                                prefix="+"
                            />
                        </Grid>
                        {/* avatar upload field */}
                        {/* <Grid item lg={6} md={8} sm={12} xs={12}>
                            <ImagesFormItem 
                                name="avatars"
                                label="user.fields.avatars"
                                storage={Storage.values.userAvatarsProfiles}
                                max={1}
                            />
                        </Grid> */}
                    </Grid>
                    <FormButtons>
                        <Button
                            variant="contained"
                            color="primary"
                            disabled={saveLoading}
                            type="button"
                            onClick={form.handleSubmit(onSubmit)}
                            startIcon={<SaveIcon />}
                            size="small"
                        >
                            Сохранить
                        </Button>
                        <Button
                            variant="contained"
                            color="secondary"
                            disabled={saveLoading}
                            onClick={onReset}
                            type="button"
                            startIcon={<UndoIcon />}
                            size="small"
                        >
                            Сброс
                        </Button>
                        {props.onCancel ? (
                            <Button
                                disabled={saveLoading}
                                onClick={()=>props.onCancel()}
                                type="button"
                                startIcon={<CloseIcon />}
                                size="small"
                            >
                                Отмена
                            </Button>
                        ) : null}
                    </FormButtons>
                </form>
            </FormProvider>
        </FormWrapper>
    )
}
