import Roles from "./roles"

const roles = Roles.values;

export default class Permissions {
    static get values() {
        return {
            operatorPermissions: {
                id: 'operatorPermissions',
                allowedRoles: [
                    roles.admin,
                    roles.operator
                ]
            },
            analystPermissions: {
                id: 'analystPermissions',
                allowedRoles: [
                    roles.admin,
                    roles.analyst
                ]
            },
            adminPermissions: {
                id: 'analystPermissions',
                allowedRoles: [
                    roles.admin,
                ]
            },
            masterEngineerPermissions: {
                id: 'masterEngineerPermissions',
                allowedRoles: [
                    roles.admin,
                    roles.masterEngineer
                ]
            },
            engineerPermissions: {
                id: 'engineerPermissions',
                allowedRoles: [
                    roles.admin,
                    roles.masterEngineer,
                    roles.engineer
                ]
            }
        }
    }
}
