import { Box, Table, TableBody, TableCell, TableHead, TablePagination, TableRow } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";

import TableCellCustom from "view/table/TableCellCustom";
import selectors from 'modules/todolist/todolistSelectors';
import actions from 'modules/todolist/todolistActions';
import Spinner from "view/shared/Spinner";
import Pagination from "view/table/Pagination";

export default function TodoListTable(){
    const dispatch = useDispatch();

    const loading = useSelector(selectors.selectLoading);
    const hasRows = useSelector(selectors.selectHasRows);
    const rows = useSelector(selectors.selectRows);
    const pagination = useSelector(selectors.selectPagination);

    const doChangePagination = (pagination) => {
        // dispatch(actions.doChangePagination(pagination));
    };

    const onChangeRowsPerPage = (pageSize) => {
        pagination.onChange({
          current: 1,
          pageSize: pageSize || 10,
        });
      };

    return(
        <>
            <Box
                style={{
                    display: "block",
                    width: '100%',
                    overflowX: 'auto'
                }}
            >
                <Table
                    style={{
                        borderRadius: '5px',
                        border: '1px solid rgb(224, 224, 224)',
                        borderCollapse: 'initial'
                    }}
                >
                    <TableHead>
                        <TableRow>
                            <TableCellCustom>
                                Имя Пользователя
                            </TableCellCustom>
                            <TableCellCustom>
                                Email
                            </TableCellCustom>
                            <TableCellCustom>
                                Текст Задачи
                            </TableCellCustom>
                            <TableCellCustom>
                                Статус
                            </TableCellCustom>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {loading && (
                            <TableRow>
                                <TableCell colSpan={100}>
                                    <Spinner />
                                </TableCell>
                            </TableRow>
                        )}
                        {!loading && !hasRows && (
                            <TableRow>
                                <TableCell colSpan={100}>
                                    <div
                                        style={{
                                            display: 'flex',
                                            justifyContent: 'center'
                                        }}
                                    >
                                        Пусто
                                    </div>
                                </TableCell>
                            </TableRow>
                        )}
                    </TableBody>
                </Table>
            </Box>
            <TablePagination
                rowsPerPageOptions={[5, 10, 25]}
                component="div"
                count={rows.length}
                rowsPerPage={10}
                page={pagination.current-1}
                onPageChange={doChangePagination}
                onRowsPerPageChange={(event) =>
                    onChangeRowsPerPage(+event.target.value)}
            />
            {/* <Pagination
                onChange={doChangePagination}
                disabled={loading}
                pagination={pagination}
            >

            </Pagination> */}
        </>
    )
}
